mapper.insert:
insert into qna
(member_no, title, content, category)
values(#{memberNo}, #{title}, #{content}, #{category});

mapper.update:
update qna 
set view = view + 1
where qna_id = #{qnaId}

update qna
set reply_count = (select count(*) from qna_reply where qna_id = #{qnaId})
where qna_id = #{qnaId};
