mapper.insert:
insert into qna
(member_no, title, content, category)
values(#{memberNo}, #{title}, #{content}, #{category});

mapper.select:
		SELECT *
		FROM (
    	SELECT ROW_NUMBER() OVER (order by `date` desc) AS qna_no, q.*
    	FROM qna q
    	) AS temp
		WHERE qna_no BETWEEN #{start} AND #{end};

mapper.update:
update qna 
set view = view + 1
where qna_id = #{qnaId}
